import React, { useState } from 'react'
import { View, Text, StyleSheet, TouchableOpacity } from 'react-native'
import { NativeBaseProvider,Button, Box,Image,AspectRatio,Icon } from 'native-base'
import {useNavigation} from '@react-navigation/native'
import InputsLogin from './Componentes/InpuntsLogin'
import {FontAwesome5} from '@expo/vector-icons'

function Login(){

    const[username, setUsername] = useState('');

    const[password, setPassword] = useState('');
          



    const navigation = useNavigation();


    return(
        <View style={styles.container}>
            <View style={styles.logoPos}>
                <Image 
                style={styles.logo}
               
                alt="image" />

            </View>
            <View style={styles.middle}>
                <Text style={styles.loginText}>Login</Text>
            </View>
            <InputsLogin value={username} setValue={setUsername}
             InputLeftElement={
                <Icon
                   as={<FontAwesome5  name="user"/>}
                   size="sm"
                   m={2}
                   _ligth={{
                       color:'black'
                   }}
                   _dark={{
                    color:'white'
                   }}
                />
            }
            placeholder="Usuario o email"
             />
            <InputsLogin value={password} setValue={setPassword}
                          InputLeftElement={
                            <Icon
                               as={<FontAwesome5  name="key"/>}
                               size="sm"
                               m={2}
                               _ligth={{
                                   color:'black'
                               }}
                               _dark={{
                                color:'white'
                               }}
                            />
                        }
                        placeholder="contraseña"
                        secureTextEntry={true}
                        />
            <View style={styles.buttonStyle}>
                <Button
                 onPress={()=>navigation.navigate("Profile")}
                style={styles.buttonDesing}>
                    LOGIN
                </Button>
            </View>
            <View style={styles.text2}>
                <TouchableOpacity onPress={()=>navigation.navigate("ForgetPassword")}>
                <Text style={styles.singupText}>Olvidaste tu Contraseña?</Text>
                </TouchableOpacity>
            </View>
            
            <View style={styles.text2}>
                <Text >No tienes cuenta?</Text>
                <TouchableOpacity onPress={()=>navigation.navigate("SingUp")}>
                <Text style={styles.singupText}>REGISTRATE</Text>
                </TouchableOpacity>
            </View>
            
            <View style={styles.lineStyle}>
                <View style={{flex:1, height:1, backgroundColor:'black'}}/>
                <View>
                    <Text style={{width:50, textAlign:'center'}}>Or</Text>
                </View>
                <View style={{flex:1, height:1, backgroundColor:'black'}}/>
            </View>

            <View style={styles.boxStyle}>
                <Box 
                
                style={{width:60, height:60, marginLeft:80}}
                shadow={3}
                _ligth={{
                    color:'black'
                }}
                _dark={{
                 color:'white'
                }}
                >
                    <AspectRatio ratio={1/1}>
                        <Image
                        roundedTop='lg'
                        source={{
                            uri:"https://cdn-icons-png.flaticon.com/512/2965/2965278.png",
                        }}
                        alt="image"
                        />
                    </AspectRatio>
                </Box>
                <Box 
                onPress={()=>navigation.navigate('#')}
                style={{width:60, height:60, marginLeft:100}}
                shadow={3}
                _ligth={{
                    color:'black'
                }}
                _dark={{
                 color:'white'
                }}
                >
                    <AspectRatio ratio={1/1}>
                        <Image
                        roundedTop='lg'
                        source={{
                            uri:"data:image/png;base64,",
                        }}
                        alt="image"
                        />
                    </AspectRatio>
                </Box>
            </View>
        </View>
    )
}
export default () => {
    return (
      <NativeBaseProvider>
        <Login/>
      </NativeBaseProvider>
    )
  }



  const styles = StyleSheet.create({
      container:{
          flex:1,
          backgroundColor:"#fff",
      },
      loginText:{
          fontSize:30,
          fontWeight: "bold"
      },
      middle:{
          alignItems:'center',
      },
      text2:{
          paddingTop: 10,
          alignItems:'center',
      },
      singupText:{
        fontWeight: "bold",
        paddingTop:5,
      },
      emailInput:{
          marginTop:10,
          marginRight:5,
      },
      buttonStyle:{
          marginRight:15,
          marginLeft:15,
          marginTop:30,
      },
      buttonStyleX:{
          marginTop:12,
          marginRight:15,
          marginLeft:15,
      },
      buttonDesing:{
          backgroundColor:"#026efd"
      },
      lineStyle:{
          flexDirection:'row',
          marginTop:30,
          marginLeft:15,
          marginRight:15,
          alignItems:'center'
      },
      lineStyle:{
          flexDirection:'row',
          marginTop:30,
          marginLeft:15,
          marginRight:15,
          justifyContent:"space-around"
    },
      logo: {
          width: 100,
          height: 100,
          resizeMode:'cover'
      },
      tinylogo: {
          width: 70,
          height: 70,
    },
    boxStyle:{
          flexDirection:'row'   
    },
    logoPos:{
          alignItems:'center',
          marginTop:100,
    }
  })
